public void RemoveUsersFromRoles(string[] usernames, string[] rolenames)\n{\n    using (var context = new YourDataContext())\n    {\n        // Get the roles matching the specified role names\n        var roles = context.Roles.Where(r => rolenames.Contains(r.Rolename)).Select(r => r.RecordID).ToList();\n\n        // Get the users matching the specified usernames\n        var users = context.Users.Where(u => usernames.Contains(u.Username)).Select(u => u.RecordID).ToList();\n\n        // Find the UsersInRole records that match both the specified users and roles\n        var usersInRoles = context.UsersInRoles\n            .Where(uir => roles.Contains(uir.RoleID) && users.Contains(uir.UserID));\n\n        // Remove the matching UsersInRole records\n        context.UsersInRoles.DeleteAllOnSubmit(usersInRoles);\n        \n        // Submit the changes to the database\n        context.SubmitChanges();\n    }\n}