public int BinarySearch(int[] array, int target) {\n    int low = 0;\n    int high = array.Length - 1;\n\n    while (low <= high) {\n        int mid = low + ((high - low) >> 1);\n        if (array[mid] == target) {\n            return mid;\n        } else if (array[mid] < target) {\n            low = mid + 1;\n        } else {\n            high = mid - 1;\n        }\n    }\n\n    return -1; // Target not found\n}