using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic void InsertSurveyResponse(string radioSelection, string comments)\n{\n    string connectionString = "your_connection_string_here";\n    string query = "INSERT INTO SurveyResponses (RadioSelection, Comments) VALUES (@RadioSelection, @Comments)";\n\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        using (SqlCommand command = new SqlCommand(query, connection))\n        {\n            command.Parameters.Add("@RadioSelection", SqlDbType.NVarChar).Value = radioSelection;\n            command.Parameters.Add("@Comments", SqlDbType.NVarChar).Value = comments;\n\n            connection.Open();\n            command.ExecuteNonQuery();\n        }\n    }\n}