private void Window_KeyDown(object sender, System.Windows.Input.KeyEventArgs e)\n{\n    if (e.Key == System.Windows.Input.Key.Escape)\n    {\n        // Assuming your grids are named grid1, grid2, ..., grid15\n        for (int i = 1; i <= 15; i++)\n        {\n            var grid = (Grid)this.FindName($"grid{i}");\n            if (grid != null && grid.IsVisible)\n            {\n                grid.BeginStoryboard((Storyboard)this.FindResource("theVisibleOne_Hide"));\n                break;\n            }\n        }\n    }\n}