// Configure MassTransit with MSMQ\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        var busControl = Bus.Factory.CreateUsingMsmq(cfg =>\n        {\n            cfg.ReceiveEndpoint("task_queue", e =>\n            {\n                e.Handler<TaskMessage>(context =>\n                {\n                    return Console.Out.WriteLineAsync($"Received Task: {context.Message.TaskName}");\n                });\n            });\n        });\n\n        busControl.Start();\n\n        try\n        {\n            Console.WriteLine("Press any key to exit");\n            Console.ReadKey();\n        }\n        finally\n        {\n            busControl.Stop();\n        }\n    }\n}\n\npublic class TaskMessage\n{\n    public string TaskName { get; set; }\n}