using System.Net;\nusing System.Net.Mail;\n\npublic void SendEmail()\n{\n    string to = "recipient@example.com";\n    string from = "your-email@example.com";\n    string subject = "Subject";\n    string body = "Email body content";\n\n    MailMessage mail = new MailMessage(from, to);\n    mail.Subject = subject;\n    mail.Body = body;\n\n    SmtpClient smtpClient = new SmtpClient("smtp.your-email-provider.com", 25);\n    smtpClient.Credentials = new NetworkCredential("your-username", "your-password");\n    smtpClient.EnableSsl = true; // Set this based on your provider's requirement\n\n    try\n    {\n        smtpClient.Send(mail);\n        Console.WriteLine("Email sent successfully.");\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine($"Failed to send email: {ex.Message}");\n    }\n}