// Assuming 'A' is your original entity and 'APrime' is your new entity with filled child collections\n\n// First, make sure that 'A' and 'APrime' are of the same type and have the same primary key\nif(A.PrimaryKey == APrime.PrimaryKey)\n{\n    // Merge the field values of APrime into A\n    A.Fields.Merge(APrime.Fields, true);\n\n    // Merge child collection B\n    A.BCollection.Clear();\n    foreach(var b in APrime.BCollection)\n    {\n        A.BCollection.Add(b);\n    }\n\n    // Merge child collection C\n    A.CCollection.Clear();\n    foreach(var c in APrime.CCollection)\n    {\n        A.CCollection.Add(c);\n    }\n}