using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // P/Invoke to NetCDF C library\n    [DllImport("netcdf.dll", CallingConvention = CallingConvention.Cdecl)]\n    public static extern int nc_open(string path, int mode, out int ncid);\n\n    [DllImport("netcdf.dll", CallingConvention = CallingConvention.Cdecl)]\n    public static extern int nc_close(int ncid);\n\n    static void Main()\n    {\n        int ncid;\n        string filePath = "example.nc";\n        \n        int retval = nc_open(filePath, 0, out ncid);\n        if (retval != 0)\n        {\n            Console.WriteLine("Error opening file.");\n            return;\n        }\n\n        Console.WriteLine("File opened successfully with ncid: " + ncid);\n        \n        retval = nc_close(ncid);\n        if (retval != 0)\n        {\n            Console.WriteLine("Error closing file.");\n            return;\n        }\n\n        Console.WriteLine("File closed successfully.");\n    }\n}