using System;\nusing System.Windows;\nusing System.Windows.Input;\nusing System.Windows.Media.Imaging;\nusing System.IO;\n\npublic partial class MainWindow : Window\n{\n    // Path to the custom cursor file\n    private const string HybridCursorPath = @"path\to\hybrid_cursor.cur";\n\n    public MainWindow()\n    {\n        InitializeComponent();\n    }\n\n    private void StartLongRunningOperation()\n    {\n        // Set the custom cursor\n        Mouse.OverrideCursor = new Cursor(HybridCursorPath);\n\n        // Start the background operation\n        BackgroundWorker worker = new BackgroundWorker();\n        worker.DoWork += (s, e) => {\n            // Simulate long running operation\n            System.Threading.Thread.Sleep(5000);\n        };\n        worker.RunWorkerCompleted += (s, e) => {\n            // Reset the cursor back to default once done\n            Mouse.OverrideCursor = null;\n        };\n        worker.RunWorkerAsync();\n    }\n\n    private void CancelOperation()\n    {\n        // Cancel the background operation and reset the cursor\n        // Logic to cancel the operation goes here\n        Mouse.OverrideCursor = null;\n    }\n}