using System;\nusing System.Collections.Generic;\nusing System.Collections.ObjectModel;\n\npublic class DataForm { }\n\npublic static class DataFormSubject\n{\n    private static List<DataForm> dataForms = new List<DataForm>();\n    private static ReadOnlyCollection<DataForm> readOnlyDataForms = new ReadOnlyCollection<DataForm>(dataForms);\n\n    public delegate void DataFormsUpdatedHandler(object sender, EventArgs e);\n    public static event DataFormsUpdatedHandler DataFormsUpdatedEvent;\n\n    public static ReadOnlyCollection<DataForm> DataForms\n    {\n        get { return readOnlyDataForms; }\n    }\n\n    public static void AddDataForm(DataForm df)\n    {\n        dataForms.Add(df);\n        OnDataFormsUpdated(EventArgs.Empty);\n    }\n\n    public static void RemoveDataForm(DataForm df)\n    {\n        dataForms.Remove(df);\n        OnDataFormsUpdated(EventArgs.Empty);\n    }\n\n    private static void OnDataFormsUpdated(EventArgs e)\n    {\n        DataFormsUpdatedEvent?.Invoke(null, e);\n    }\n}