[HttpPost]\n   public IActionResult CreateTracker([FromBody] TrackerRequest request)\n   {\n       // Create new Tracker\n       var tracker = new Tracker\n       {\n           UserName = "randomperson13",\n           TotalRequests = 1,\n           LastRequest = DateTime.Now,\n           Status = "pending"\n       };\n       \n       // Save Tracker to database and get TrackerID\n       _context.Trackers.Add(tracker);\n       _context.SaveChanges();\n\n       // Add TrackerItems\n       foreach (var (id, title) in request.Ids.Zip(request.Titles, (id, title) => (id, title)))\n       {\n           var trackerItem = new TrackerItem\n           {\n               TrackerID = tracker.TrackerID,\n               ID = id,\n               Title = title\n           };\n           _context.TrackerItems.Add(trackerItem);\n       }\n\n       _context.SaveChanges();\n\n       return Ok();\n   }