using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nnamespace LinqExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            List<int> firstList = new List<int> { 1, 2, 3, 4, 5 };\n            List<int> secondList = new List<int> { 3, 4, 5, 6, 7 };\n\n            IEnumerable<int> result = firstList.Except(secondList);\n\n            Console.WriteLine("Items in the first list but not in the second list:");\n            foreach (int item in result)\n            {\n                Console.WriteLine(item);\n            }\n        }\n    }\n}