using System;\nusing System.Collections.Generic;\nusing System.Web.UI.WebControls;\n\nnamespace GridViewExample\n{\n    public partial class _Default : System.Web.UI.Page\n    {\n        protected void Page_Load(object sender, EventArgs e)\n        {\n            if (!IsPostBack)\n            {\n                BindGridView();\n            }\n        }\n\n        private void BindGridView()\n        {\n            // Sample data\n            var data = new List<Item>\n            {\n                new Item { ItemName = "Item 1", Amount = 100 },\n                new Item { ItemName = "Item 2", Amount = 200 },\n                new Item { ItemName = "Item 3", Amount = 150 }\n            };\n\n            GridView1.DataSource = data;\n            GridView1.DataBind();\n        }\n\n        private decimal runningTotal = 0;\n\n        protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n        {\n            if (e.Row.RowType == DataControlRowType.DataRow)\n            {\n                var amount = Convert.ToDecimal(DataBinder.Eval(e.Row.DataItem, "Amount"));\n                runningTotal += amount;\n            }\n            else if (e.Row.RowType == DataControlRowType.Footer)\n            {\n                var lblTotal = (Label)e.Row.FindControl("lblTotal");\n                lblTotal.Text = runningTotal.ToString("C");\n            }\n        }\n\n        public class Item\n        {\n            public string ItemName { get; set; }\n            public decimal Amount { get; set; }\n        }\n    }\n}