using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class FileEncryption\n{\n    public static void Main()\n    {\n        string originalFilePath = "path/to/your/original/file.txt";\n        string encryptedFilePath = "path/to/your/encrypted/file.enc";\n        string decryptedFilePath = "path/to/your/decrypted/file.txt";\n        \n        // Create a new key and IV\n        using (Aes aes = Aes.Create())\n        {\n            byte[] key = aes.Key;\n            byte[] iv = aes.IV;\n\n            // Encrypt the file\n            EncryptFile(originalFilePath, encryptedFilePath, key, iv);\n\n            // Decrypt the file\n            DecryptFile(encryptedFilePath, decryptedFilePath, key, iv);\n        }\n    }\n\n    public static void EncryptFile(string inputFile, string outputFile, byte[] key, byte[] iv)\n    {\n        using (Aes aes = Aes.Create())\n        {\n            aes.Key = key;\n            aes.IV = iv;\n            ICryptoTransform encryptor = aes.CreateEncryptor(aes.Key, aes.IV);\n\n            using (FileStream inputFileStream = new FileStream(inputFile, FileMode.Open, FileAccess.Read))\n            using (FileStream outputFileStream = new FileStream(outputFile, FileMode.Create, FileAccess.Write))\n            using (CryptoStream cryptoStream = new CryptoStream(outputFileStream, encryptor, CryptoStreamMode.Write))\n            {\n                inputFileStream.CopyTo(cryptoStream);\n            }\n        }\n    }\n\n    public static void DecryptFile(string inputFile, string outputFile, byte[] key, byte[] iv)\n    {\n        using (Aes aes = Aes.Create())\n        {\n            aes.Key = key;\n            aes.IV = iv;\n            ICryptoTransform decryptor = aes.CreateDecryptor(aes.Key, aes.IV);\n\n            using (FileStream inputFileStream = new FileStream(inputFile, FileMode.Open, FileAccess.Read))\n            using (FileStream outputFileStream = new FileStream(outputFile, FileMode.Create, FileAccess.Write))\n            using (CryptoStream cryptoStream = new CryptoStream(inputFileStream, decryptor, CryptoStreamMode.Read))\n            {\n                cryptoStream.CopyTo(outputFileStream);\n            }\n        }\n    }\n}