using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class DataAccess\n{\n    private static string ConnectionString = "your_connection_string_here";\n\n    public static int GetBookmarkId()\n    {\n        int bookmarkId = 0;\n\n        using (SqlConnection connection = new SqlConnection(ConnectionString))\n        {\n            using (SqlCommand command = new SqlCommand($"{ApplicationSchema}.bookmarks.procGet_Bookmark_Id", connection))\n            {\n                command.CommandType = CommandType.StoredProcedure;\n\n                // Add the output parameter\n                SqlParameter outParam = new SqlParameter("@bookmarkId", SqlDbType.Int)\n                {\n                    Direction = ParameterDirection.Output\n                };\n                command.Parameters.Add(outParam);\n\n                connection.Open();\n                command.ExecuteNonQuery();\n\n                // Retrieve the value of the output parameter\n                bookmarkId = (int)outParam.Value;\n            }\n        }\n\n        return bookmarkId;\n    }\n}