using System;\nusing System.Globalization;\n\npublic class DateExample\n{\n    public static readonly DateTime ADate;\n\n    static DateExample()\n    {\n        // Specify the date in a culture-invariant format\n        ADate = DateTime.ParseExact("2004-12-31", "yyyy-MM-dd", CultureInfo.InvariantCulture);\n    }\n\n    public static void Main()\n    {\n        Console.WriteLine(ADate.ToString("d", CultureInfo.InvariantCulture)); // prints 12/31/2004\n    }\n}