using System;\nusing System.Linq;\nusing System.Data.Linq;\n\npublic class Subscription\n{\n    public int Id { get; set; }\n    public DateTime Expires { get; set; }\n}\n\npublic class DbContext : DataContext\n{\n    public Table<Subscription> Subscriptions;\n\n    public DbContext(string connectionString) : base(connectionString) { }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "YourConnectionStringHere";\n        using (var dbContext = new DbContext(connectionString))\n        {\n            var subscriptions = from subscription in dbContext.Subscriptions\n                                where subscription.Expires > DateTime.Now\n                                select subscription;\n\n            var futureSubscriptions = from subscription in dbContext.Subscriptions\n                                      where subscription.Expires > DateTime.Now.AddDays(2)\n                                      select subscription;\n        }\n    }\n}