using System;\nusing System.ServiceModel;\nusing System.ServiceModel.Web;\n\n[ServiceContract]\npublic interface ICustomerService\n{\n    [OperationContract]\n    [WebGet(ResponseFormat = WebMessageFormat.Json, UriTemplate = "/customers")]\n    List<Customer> GetCustomers();\n}\n\npublic class CustomerService : ICustomerService\n{\n    public List<Customer> GetCustomers()\n    {\n        // Sample data\n        return new List<Customer>\n        {\n            new Customer { Id = 1, Name = "John Doe", Email = "john.doe@example.com" },\n            new Customer { Id = 2, Name = "Jane Smith", Email = "jane.smith@example.com" }\n        };\n    }\n}\n\npublic class Customer\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public string Email { get; set; }\n}\n\npublic class Program\n{\n    static void Main(string[] args)\n    {\n        WebServiceHost host = new WebServiceHost(typeof(CustomerService), new Uri("http://localhost:8080/"));\n        host.Open();\n        Console.WriteLine("Service is running...");\n        Console.ReadLine();\n        host.Close();\n    }\n}