using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\npublic class Program\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    public static async Task Main(string[] args)\n    {\n        var url = "https://example.com/api/resource"; // Replace with your URL\n        var data = "{ \"key\": \"value\" }"; // Replace with your data in JSON format\n\n        var content = new StringContent(data, Encoding.UTF8, "application/json");\n\n        var response = await client.PutAsync(url, content);\n\n        if (response.IsSuccessStatusCode)\n        {\n            Console.WriteLine("Data successfully updated.");\n        }\n        else\n        {\n            Console.WriteLine($"Failed to update data. Status code: {response.StatusCode}");\n        }\n    }\n}