using System;\nusing iTextSharp.text.pdf;\nusing iTextSharp.text.pdf.parser;\n\nclass Program\n{\n    static void ExtractAnnotations(string filePath)\n    {\n        using (PdfReader reader = new PdfReader(filePath))\n        {\n            for (int i = 1; i <= reader.NumberOfPages; i++)\n            {\n                PdfDictionary pageDict = reader.GetPageN(i);\n                PdfArray annots = pageDict.GetAsArray(PdfName.ANNOTS);\n                if (annots != null)\n                {\n                    foreach (PdfObject annot in annots.ArrayList)\n                    {\n                        PdfDictionary annotDict = (PdfDictionary)PdfReader.GetPdfObject(annot);\n                        PdfString content = annotDict.GetAsString(PdfName.CONTENTS);\n                        if (content != null)\n                        {\n                            Console.WriteLine("Annotation on page {0}: {1}", i, content.ToString());\n                        }\n                    }\n                }\n            }\n        }\n    }\n\n    static void Main()\n    {\n        string filePath = "path_to_your_pdf_file.pdf";\n        ExtractAnnotations(filePath);\n    }\n}