using System.Net.Mail;\n\npublic void SendConfirmationEmail(string toEmail, string confirmationLink)\n{\n    string subject = "Email Confirmation";\n    string body = $"Please confirm your email by clicking on the following link: <a href='{confirmationLink}'>Confirm Email</a>";\n\n    MailMessage mailMessage = new MailMessage("your-email@example.com", toEmail)\n    {\n        Subject = subject,\n        Body = body,\n        IsBodyHtml = true\n    };\n\n    SmtpClient smtpClient = new SmtpClient("your-smtp-server");\n    smtpClient.Send(mailMessage);\n}