using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Import the FindWindow function from user32.dll\n    [DllImport("user32.dll", SetLastError = true)]\n    private static extern IntPtr FindWindow(string lpClassName, string lpWindowName);\n\n    // Import the SetWindowText function from user32.dll\n    [DllImport("user32.dll", SetLastError = true)]\n    private static extern bool SetWindowText(IntPtr hWnd, string lpString);\n\n    static void Main()\n    {\n        // Start the Notepad process\n        Process notepad = Process.Start("notepad.exe");\n        notepad.WaitForInputIdle(); // Wait for the process to be ready for input\n\n        // Find the window handle of the Notepad window\n        IntPtr hWnd = FindWindow(null, "Untitled - Notepad");\n\n        if (hWnd != IntPtr.Zero)\n        {\n            // Change the window title\n            SetWindowText(hWnd, "New Notepad Name");\n            Console.WriteLine("Window title changed successfully.");\n        }\n        else\n        {\n            Console.WriteLine("Failed to find the Notepad window.");\n        }\n    }\n}