[TestMethod]\npublic void SecureSocketLayerSetToTrue()\n{\n    var ldapConnection = new LdapConnection(\n                            new LdapDirectoryIdentifier("ldap.test.com", 636));\n    \n    // Set SecureSocketLayer before any operations\n    ldapConnection.SessionOptions.SecureSocketLayer = true;\n\n    // Assert that the property is set correctly\n    Assert.IsTrue(ldapConnection.SessionOptions.SecureSocketLayer);\n\n    // Optionally, you can bind the connection to check if SSL works properly\n    ldapConnection.Credential = new NetworkCredential("username", "password");\n    ldapConnection.AuthType = AuthType.Basic;\n    ldapConnection.Bind(); // This will throw an exception if SSL is not correctly configured\n}