using System;\nusing System.Drawing.Printing;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        PrintDocument printDoc = new PrintDocument();\n        printDoc.PrintPage += new PrintPageEventHandler(PrintPage);\n        printDoc.DocumentName = "MyDocument.txt"; // Set the document name\n        printDoc.Print();\n    }\n\n    private static void PrintPage(object sender, PrintPageEventArgs ev)\n    {\n        string filePath = @"C:\path\to\your\file.txt";\n        string text = File.ReadAllText(filePath);\n        ev.Graphics.DrawString(text, new Font("Arial", 12), Brushes.Black, ev.MarginBounds);\n    }\n}