public void SendPasswordResetEmail(string userEmail, string resetLink)\n   {\n       // Code to send email\n       // Example using SmtpClient:\n       SmtpClient client = new SmtpClient("smtp.your-email-provider.com");\n       MailMessage mailMessage = new MailMessage();\n       mailMessage.From = new MailAddress("no-reply@yourwebsite.com");\n       mailMessage.To.Add(userEmail);\n       mailMessage.Subject = "Password Reset Request";\n       mailMessage.Body = $"Please reset your password by clicking on the following link: {resetLink}";\n       client.Send(mailMessage);\n   }