using System;\nusing Microsoft.Office.Interop.MSProject;\n\nnamespace ProjectInteropExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            Application projectApp = null;\n            try\n            {\n                // Initialize the Project application\n                projectApp = new Application();\n                projectApp.Visible = true;\n\n                // Open a project file\n                projectApp.FileOpen(@"C:\Path\To\Your\ProjectFile.mpp");\n\n                // Access the active project\n                Project activeProject = projectApp.ActiveProject;\n\n                // Example: Iterate through tasks and print their names\n                foreach (Task task in activeProject.Tasks)\n                {\n                    if (task != null)\n                    {\n                        Console.WriteLine($"Task Name: {task.Name}");\n                    }\n                }\n\n                // Perform additional operations as needed...\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n            finally\n            {\n                if (projectApp != null)\n                {\n                    // Optionally, quit the Project application\n                    projectApp.Quit(PjSaveType.pjDoNotSave);\n                }\n            }\n        }\n    }\n}