using System;\nusing System.IO;\n\npublic class WebsiteBlocker\n{\n    private static string hostsFilePath = @"C:\Windows\System32\drivers\etc\hosts";\n\n    public static void BlockWebsite(string domain)\n    {\n        try\n        {\n            string entry = $"127.0.0.1 {domain}";\n            using (StreamWriter sw = File.AppendText(hostsFilePath))\n            {\n                sw.WriteLine(entry);\n            }\n            Console.WriteLine($"{domain} has been blocked.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error blocking website: {ex.Message}");\n        }\n    }\n\n    public static void UnblockWebsite(string domain)\n    {\n        try\n        {\n            string[] lines = File.ReadAllLines(hostsFilePath);\n            using (StreamWriter sw = new StreamWriter(hostsFilePath))\n            {\n                foreach (string line in lines)\n                {\n                    if (!line.Contains(domain))\n                    {\n                        sw.WriteLine(line);\n                    }\n                }\n            }\n            Console.WriteLine($"{domain} has been unblocked.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error unblocking website: {ex.Message}");\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        WebsiteBlocker.BlockWebsite("www.example.com");\n        // To unblock the website, call:\n        // WebsiteBlocker.UnblockWebsite("www.example.com");\n    }\n}