using System;\nusing System.Data.SqlClient;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class DecryptionHelper\n{\n    private static readonly byte[] Key = Encoding.UTF8.GetBytes("YourEncryptionKey");\n    private static readonly byte[] IV = Encoding.UTF8.GetBytes("YourIV");\n\n    public static string DecryptString(string cipherText)\n    {\n        byte[] cipherBytes = Convert.FromBase64String(cipherText);\n        using (Aes aesAlg = Aes.Create())\n        {\n            aesAlg.Key = Key;\n            aesAlg.IV = IV;\n            ICryptoTransform decryptor = aesAlg.CreateDecryptor(aesAlg.Key, aesAlg.IV);\n\n            using (var msDecrypt = new System.IO.MemoryStream(cipherBytes))\n            {\n                using (var csDecrypt = new CryptoStream(msDecrypt, decryptor, CryptoStreamMode.Read))\n                {\n                    using (var srDecrypt = new System.IO.StreamReader(csDecrypt))\n                    {\n                        return srDecrypt.ReadToEnd();\n                    }\n                }\n            }\n        }\n    }\n\n    public static (string, string) RetrieveCredentials(int userId)\n    {\n        string encryptedUsername = string.Empty;\n        string encryptedPassword = string.Empty;\n\n        using (SqlConnection connection = new SqlConnection("YourConnectionString"))\n        {\n            string query = "SELECT Username, Password FROM YourTable WHERE UserID = @UserID";\n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                command.Parameters.AddWithValue("@UserID", userId);\n                connection.Open();\n                using (SqlDataReader reader = command.ExecuteReader())\n                {\n                    if (reader.Read())\n                    {\n                        encryptedUsername = reader["Username"].ToString();\n                        encryptedPassword = reader["Password"].ToString();\n                    }\n                }\n            }\n        }\n\n        string username = DecryptString(encryptedUsername);\n        string password = DecryptString(encryptedPassword);\n        return (username, password);\n    }\n}