[OperationContract]\n[WebGet(\n    UriTemplate = "/appointments/{year}/{month}/{day}",\n    RequestFormat = WebMessageFormat.Xml,\n    ResponseFormat = WebMessageFormat.Xml,\n    BodyStyle = WebMessageBodyStyle.Bare\n)]\n[ContentType("text/xml")]\npublic XElement ListAppointments(string year, string month, string day)\n{\n    try\n    {\n        int iYear = int.TryParse(year, out int tempYear) ? tempYear : DateTime.Now.Year;\n        int iMonth = int.TryParse(month, out int tempMonth) ? tempMonth : DateTime.Now.Month;\n        int iDay = int.TryParse(day, out int tempDay) ? tempDay : DateTime.Now.Day;\n\n        var now = new DateTime(iYear, iMonth, iDay).Date;\n        return GetAppointments(now).ToXml();\n    }\n    catch (System.Exception ex)\n    {\n        return new XElement("exception", ex.ToString());\n    }\n}