// Assuming you have two TableAdapters: ProductsTableAdapter and PricesTableAdapter\nProductsTableAdapter productsAdapter = new ProductsTableAdapter();\nPricesTableAdapter pricesAdapter = new PricesTableAdapter();\n\n// Get changes for the Products table\nvar modifiedProducts = dTiendasDs.Productos.GetChanges(DataRowState.Modified) as dTiendasDs.ProductosDataTable;\nif (modifiedProducts != null)\n{\n    // Update the Products table\n    productsAdapter.Update(modifiedProducts);\n}\n\n// Get changes for the Prices table\nvar modifiedPrices = dTiendasDs.Precios.GetChanges(DataRowState.Modified) as dTiendasDs.PreciosDataTable;\nif (modifiedPrices != null)\n{\n    // Update the Prices table\n    pricesAdapter.Update(modifiedPrices);\n}\n\n// Accept changes after successful update\ndTiendasDs.AcceptChanges();