using System;\nusing System.Data;\nusing System.Data.SQLite;\nusing System.Windows.Forms;\n\npublic partial class MainForm : Form\n{\n    private DataTable dataTable;\n    private string connectionString = "Data Source=mydatabase.db;Version=3;";\n\n    public MainForm()\n    {\n        InitializeComponent();\n        LoadData();\n    }\n\n    private void LoadData()\n    {\n        using (SQLiteConnection conn = new SQLiteConnection(connectionString))\n        {\n            conn.Open();\n            string query = "SELECT * FROM myTable";\n            SQLiteDataAdapter adapter = new SQLiteDataAdapter(query, conn);\n            dataTable = new DataTable();\n            adapter.Fill(dataTable);\n            dataGridView.DataSource = dataTable; // Assuming you have a DataGridView named dataGridView\n        }\n    }\n\n    private void SaveData()\n    {\n        using (SQLiteConnection conn = new SQLiteConnection(connectionString))\n        {\n            conn.Open();\n            SQLiteDataAdapter adapter = new SQLiteDataAdapter("SELECT * FROM myTable", conn);\n            SQLiteCommandBuilder builder = new SQLiteCommandBuilder(adapter);\n            adapter.Update(dataTable);\n        }\n    }\n\n    private void btnSave_Click(object sender, EventArgs e)\n    {\n        SaveData();\n        MessageBox.Show("Data saved successfully!");\n    }\n}