protected override void OnInit(EventArgs e)\n{\n    base.OnInit(e);\n    \n    priceTextbox.ID = this.ID + "_price";\n    quantityTextbox.ID = this.ID + "_quantity";\n    timeTextbox.ID = this.ID + "_time";\n    submitButton.ID = this.ID + "_submit";\n    submitButton.Text = "Submit";\n\n    // Set up the MaskedEditExtender for the price textbox\n    priceMask.TargetControlID = priceTextbox.ID;\n    priceMask.ID = priceTextbox.ID + "_extender";\n    priceMask.BehaviorID = "priceMaskExtender";\n    priceMask.Mask = "99.99";\n    \n    // Add the price textbox control to the composite control\n    Controls.Add(priceTextbox);\n    Controls.Add(priceMask);\n\n    // Add a literal control to display the dollar sign\n    Literal dollarSign = new Literal();\n    dollarSign.Text = "$";\n    Controls.AddAt(Controls.IndexOf(priceTextbox), dollarSign);\n    \n    // Ensure other controls like quantityTextbox, timeTextbox, and submitButton are added\n    Controls.Add(quantityTextbox);\n    Controls.Add(timeTextbox);\n    Controls.Add(submitButton);\n}