// Initialize the DataTable and DataAdapter\nDataTable dataTable = new DataTable();\nSqlDataAdapter dataAdapter = new SqlDataAdapter("SELECT * FROM YourTable", connectionString);\nSqlCommandBuilder commandBuilder = new SqlCommandBuilder(dataAdapter);\ndataAdapter.Fill(dataTable);\nyourDataGrid.ItemsSource = dataTable.DefaultView;\n\n// Handle Save Button Click Event\nprivate void SaveButton_Click(object sender, RoutedEventArgs e)\n{\n    try\n    {\n        yourDataGrid.CommitEdit(DataGridEditingUnit.Cell, true);\n        yourDataGrid.CommitEdit(DataGridEditingUnit.Row, true);\n        dataAdapter.Update(dataTable);\n        dataTable.AcceptChanges();\n        MessageBox.Show("Changes saved successfully!");\n    }\n    catch (Exception ex)\n    {\n        MessageBox.Show($"Error saving changes: {ex.Message}");\n    }\n}