using System;\n\npublic class Program\n{\n    public enum A\n    {\n        B = 0,\n        C = 1,\n        D = 2\n    }\n\n    public static void Main()\n    {\n        string inputString = "C";\n\n        // Check if the string matches an enum value\n        if (Enum.TryParse(inputString, out A enumValue))\n        {\n            Console.WriteLine($"The string '{inputString}' matches the enum value {enumValue}.");\n        }\n        else\n        {\n            Console.WriteLine($"The string '{inputString}' does not match any enum values.");\n        }\n\n        // Convert string to corresponding enum value\n        if (Enum.TryParse<A>(inputString, out A convertedEnumValue))\n        {\n            Console.WriteLine($"Successfully converted '{inputString}' to enum value {convertedEnumValue}.");\n        }\n        else\n        {\n            Console.WriteLine($"Failed to convert '{inputString}' to an enum value.");\n        }\n    }\n}