public class DataManager\n{\n    public Data[] GetData()\n    {\n        string cacheKey = "MyDataCache";\n\n        // Try to get the data from the cache\n        var cachedData = HttpRuntime.Cache[cacheKey] as Data[];\n\n        if (cachedData == null)\n        {\n            // Data is not in the cache, retrieve it from the database\n            Data[] data = GetDataFromDatabase(); // Replace this with your actual data retrieval code\n\n            // Store the data in the cache with an absolute expiration of 10 seconds\n            HttpRuntime.Cache.Insert(\n                cacheKey, \n                data, \n                null, \n                DateTime.Now.AddSeconds(10), \n                Cache.NoSlidingExpiration\n            );\n\n            return data;\n        }\n        else\n        {\n            // Return the cached data\n            return cachedData;\n        }\n    }\n\n    private Data[] GetDataFromDatabase()\n    {\n        // Your logic to retrieve data from the database\n    }\n}