using System;\n\nclass Program\n{\n    static void Main()\n    {\n        string url = "https://www.example.com";\n\n        Uri uri = new Uri(url);\n\n        if (uri.Scheme == Uri.UriSchemeHttps)\n        {\n            Console.WriteLine("The site is running HTTPS.");\n        }\n        else\n        {\n            Console.WriteLine("The site is not running HTTPS.");\n        }\n    }\n}