using System;\nusing System.Web;\nusing System.DirectoryServices;\n\npublic partial class _Default : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Get the Windows username\n        string windowsUsername = HttpContext.Current.User.Identity.Name;\n        \n        // Perform LDAP query to get Novell username\n        string novellUsername = GetNovellUsername(windowsUsername);\n        \n        Response.Write("Novell Username: " + novellUsername);\n    }\n\n    private string GetNovellUsername(string windowsUsername)\n    {\n        string ldapPath = "LDAP://your-novell-server";\n        string novellUsername = string.Empty;\n\n        try\n        {\n            DirectoryEntry entry = new DirectoryEntry(ldapPath);\n            DirectorySearcher searcher = new DirectorySearcher(entry);\n            \n            searcher.Filter = $"(sAMAccountName={windowsUsername})";\n            searcher.PropertiesToLoad.Add("cn");\n\n            SearchResult result = searcher.FindOne();\n            if (result != null)\n            {\n                novellUsername = result.Properties["cn"][0].ToString();\n            }\n        }\n        catch (Exception ex)\n        {\n            // Handle exception\n            Console.WriteLine("Error: " + ex.Message);\n        }\n\n        return novellUsername;\n    }\n}