public ActionResult Update(MyModel model)\n{\n    if (!ModelState.IsValid)\n    {\n        // Log validation errors\n        foreach (var key in ModelState.Keys)\n        {\n            var state = ModelState[key];\n            foreach (var error in state.Errors)\n            {\n                // Here you can log the key and the error message\n                // For example: Logger.Log($"{key}: {error.ErrorMessage}");\n\n                System.Diagnostics.Debug.WriteLine($"{key}: {error.ErrorMessage}");\n            }\n        }\n        \n        // Return the view with the model to show validation errors\n        return View(model);\n    }\n\n    // Perform the update operation if model is valid\n    // ...\n\n    return RedirectToAction("Index");\n}