using System;\nusing System.Text;\n\npublic class Scrambler\n{\n    private static readonly string Alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";\n    private static readonly Random Random = new Random();\n\n    public static string Scramble(string input)\n    {\n        var scrambled = new StringBuilder(input.Length);\n\n        foreach (var character in input)\n        {\n            if (character == '.')\n            {\n                scrambled.Append('.');\n            }\n            else\n            {\n                scrambled.Append(GetRandomChar());\n            }\n        }\n\n        return scrambled.ToString();\n    }\n\n    private static char GetRandomChar()\n    {\n        int index = Random.Next(Alphabet.Length);\n        return Alphabet[index];\n    }\n\n    public static void Main(string[] args)\n    {\n        string originalLogin = "Jesse.J.James";\n        string scrambledLogin = Scramble(originalLogin);\n\n        Console.WriteLine($"Original: {originalLogin}");\n        Console.WriteLine($"Scrambled: {scrambledLogin}");\n    }\n}