using log4net;\nusing log4net.Config;\nusing System;\nusing System.IO;\nusing System.Reflection;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Specify the external configuration file path\n        var log4netConfig = new FileInfo("Log.config");\n        \n        // Configure log4net using the external configuration file\n        XmlConfigurator.Configure(log4netConfig);\n\n        // Create a logger instance\n        ILog log = LogManager.GetLogger(MethodBase.GetCurrentMethod().DeclaringType);\n\n        // Log something to test\n        log.Info("Application started.");\n\n        Console.WriteLine("Check the log file to see the logged message.");\n    }\n}