public string GenerateJwtToken(string userId)\n{\n    var securityKey = new SymmetricSecurityKey(Encoding.UTF8.GetBytes("YourSecretKey"));\n    var credentials = new SigningCredentials(securityKey, SecurityAlgorithms.HmacSha256);\n\n    var token = new JwtSecurityToken(\n        issuer: "yourdomain.com",\n        audience: "yourdomain.com",\n        claims: new[] { new Claim("userId", userId) },\n        expires: DateTime.Now.AddMinutes(30),\n        signingCredentials: credentials\n    );\n\n    return new JwtSecurityTokenHandler().WriteToken(token);\n}\n\n// Usage\nstring userId = "12345";\nstring token = GenerateJwtToken(userId);\nstring url = "http://www.anotherapplicationsite.com/somesuburl?token=" + HttpUtility.UrlEncode(token);\nResponse.Redirect(url);