public class TimingModule : IHttpModule\n   {\n       public void Init(HttpApplication context)\n       {\n           context.BeginRequest += new EventHandler(OnBeginRequest);\n           context.EndRequest += new EventHandler(OnEndRequest);\n           context.PreRequestHandlerExecute += new EventHandler(OnPreRequestHandlerExecute);\n           context.PostRequestHandlerExecute += new EventHandler(OnPostRequestHandlerExecute);\n       }\n\n       private void OnBeginRequest(object sender, EventArgs e)\n       {\n           HttpContext.Current.Items["RequestStartTime"] = DateTime.UtcNow;\n       }\n\n       private void OnPreRequestHandlerExecute(object sender, EventArgs e)\n       {\n           HttpContext.Current.Items["HandlerStartTime"] = DateTime.UtcNow;\n       }\n\n       private void OnPostRequestHandlerExecute(object sender, EventArgs e)\n       {\n           HttpContext.Current.Items["HandlerEndTime"] = DateTime.UtcNow;\n       }\n\n       private void OnEndRequest(object sender, EventArgs e)\n       {\n           DateTime requestStartTime = (DateTime)HttpContext.Current.Items["RequestStartTime"];\n           DateTime handlerStartTime = (DateTime)HttpContext.Current.Items["HandlerStartTime"];\n           DateTime handlerEndTime = (DateTime)HttpContext.Current.Items["HandlerEndTime"];\n           DateTime requestEndTime = DateTime.UtcNow;\n\n           // Log the timings as needed, e.g., to a database or log file\n           // Example logging to console:\n           System.Diagnostics.Debug.WriteLine($"Request Start: {requestStartTime}");\n           System.Diagnostics.Debug.WriteLine($"Handler Start: {handlerStartTime}");\n           System.Diagnostics.Debug.WriteLine($"Handler End: {handlerEndTime}");\n           System.Diagnostics.Debug.WriteLine($"Request End: {requestEndTime}");\n           System.Diagnostics.Debug.WriteLine($"Total Request Time: {requestEndTime - requestStartTime}");\n           System.Diagnostics.Debug.WriteLine($"Handler Execution Time: {handlerEndTime - handlerStartTime}");\n       }\n\n       public void Dispose()\n       {\n       }\n   }