using System;\nusing System.IO;\nusing System.Text;\n\npublic class UnicodeReader\n{\n    public static string ReadUnicodeString(BinaryReader reader, int byteCount)\n    {\n        // Read the specified number of bytes from the stream\n        byte[] bytes = reader.ReadBytes(byteCount);\n        \n        // Convert the byte array to a string using Unicode encoding\n        string result = Encoding.Unicode.GetString(bytes);\n        \n        return result;\n    }\n\n    public static void Main()\n    {\n        // Example byte array representing the Unicode message\n        byte[] unicodeMessage = new byte[] { 0x74, 0x00, 0x65, 0x00, 0x73, 0x00, 0x74, 0x00, 0x3F, 0x00 };\n\n        // Using a MemoryStream for demonstration purposes\n        using (MemoryStream memoryStream = new MemoryStream(unicodeMessage))\n        using (BinaryReader reader = new BinaryReader(memoryStream))\n        {\n            string message = ReadUnicodeString(reader, unicodeMessage.Length);\n            Console.WriteLine(message);  // Output: test?\n        }\n    }\n}