using System;\nusing System.Net;\nusing System.Net.NetworkInformation;\n\nclass Program\n{\n    static void Main()\n    {\n        string[] ipAddresses = { "192.168.1.1", "192.168.1.2", "192.168.1.3" };\n        CheckActiveTcpConnections(ipAddresses);\n    }\n\n    static void CheckActiveTcpConnections(string[] ipAddresses)\n    {\n        IPGlobalProperties ipProperties = IPGlobalProperties.GetIPGlobalProperties();\n        TcpConnectionInformation[] tcpConnections = ipProperties.GetActiveTcpConnections();\n\n        foreach (string ipAddress in ipAddresses)\n        {\n            IPAddress targetIP = IPAddress.Parse(ipAddress);\n            bool connectionFound = false;\n\n            foreach (TcpConnectionInformation tcpConnection in tcpConnections)\n            {\n                if (tcpConnection.RemoteEndPoint.Address.Equals(targetIP) && \n                    (tcpConnection.State == TcpState.Established))\n                {\n                    Console.WriteLine($"Active TCP connection found to IP: {ipAddress}");\n                    connectionFound = true;\n                    break;\n                }\n            }\n\n            if (!connectionFound)\n            {\n                Console.WriteLine($"No active TCP connection to IP: {ipAddress}");\n            }\n        }\n    }\n}