using Spectre.Console;\n\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            var file = AnsiConsole.Prompt(\n                new SelectionPrompt<string>()\n                    .Title("Select a file")\n                    .PageSize(10)\n                    .AddChoices(new[] { "file1.txt", "file2.txt", "file3.txt" })\n            );\n\n            AnsiConsole.MarkupLine($"You selected [green]{file}[/]");\n        }\n    }