using System;\nusing System.IO;\nusing System.Reflection;\n\npublic class EmbeddedResourceHelper\n{\n    public static string GetEmbeddedSqlScript(string resourceName)\n    {\n        var assembly = Assembly.GetExecutingAssembly();\n        using (Stream stream = assembly.GetManifestResourceStream(resourceName))\n        {\n            if (stream == null)\n                throw new ArgumentException($"Resource '{resourceName}' not found.");\n\n            using (StreamReader reader = new StreamReader(stream))\n            {\n                return reader.ReadToEnd();\n            }\n        }\n    }\n}\n\n// Usage\nclass Program\n{\n    static void Main()\n    {\n        string resourceName = "YourNamespace.YourSqlFile.sql";\n        string sqlScript = EmbeddedResourceHelper.GetEmbeddedSqlScript(resourceName);\n\n        // Use the SQL script as needed, e.g., to execute against a database\n        Console.WriteLine(sqlScript);\n    }\n}