using System;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        var dbRecords = new[] \n        {\n            "The quick brown fox jumps over the lazy dog"\n        };\n        \n        var userInput = "brown cow";\n        var userWords = userInput.Split(' ');\n\n        var matchedRecord = dbRecords\n            .Select(record => new\n            {\n                Record = record,\n                MatchCount = userWords.Count(word => record.Contains(word, StringComparison.OrdinalIgnoreCase))\n            })\n            .OrderByDescending(r => r.MatchCount)\n            .FirstOrDefault();\n\n        Console.WriteLine(matchedRecord?.Record);\n    }\n}