using System;\nusing System.Linq.Expressions;\nusing System.Linq.Dynamic.Core;\n\nnamespace ExpressionParser\n{\n    public class Person\n    {\n        public string Name { get; set; }\n        public int Age { get; set; }\n        public int Weight { get; set; }\n        public DateTime FavouriteDay { get; set; }\n    }\n\n    class Program\n    {\n        static void Main()\n        {\n            const string expression = "(Age > 3 AND Weight > 50) OR Age < 3";\n            var parameter = Expression.Parameter(typeof(Person), "Person");\n            var lambdaExpression = DynamicExpressionParser.ParseLambda(new[] { parameter }, typeof(bool), expression);\n\n            var bob = new Person\n            {\n                Name = "Bob",\n                Age = 30,\n                Weight = 213,\n                FavouriteDay = new DateTime(2000, 1, 1)\n            };\n\n            var result = lambdaExpression.Compile().DynamicInvoke(bob);\n            Console.WriteLine(result); // Outputs: True\n        }\n    }\n}