using System;\nusing System.IO;\nusing iTextSharp.text.pdf;\n\nclass Program\n{\n    static void Main()\n    {\n        string inputPdfPath = "input.pdf";\n        string outputPdfPath = "output.pdf";\n\n        // Define the new page order, e.g., [3, 1, 2] means page 3, then page 1, then page 2.\n        int[] pageOrder = { 3, 1, 2 };\n\n        ReorderPdfPages(inputPdfPath, outputPdfPath, pageOrder);\n    }\n\n    static void ReorderPdfPages(string inputPdfPath, string outputPdfPath, int[] pageOrder)\n    {\n        using (PdfReader reader = new PdfReader(inputPdfPath))\n        {\n            using (Document document = new Document(reader.GetPageSizeWithRotation(1)))\n            {\n                using (PdfCopy copy = new PdfCopy(document, new FileStream(outputPdfPath, FileMode.Create)))\n                {\n                    document.Open();\n                    foreach (int pageNum in pageOrder)\n                    {\n                        copy.AddPage(copy.GetImportedPage(reader, pageNum));\n                    }\n                }\n            }\n        }\n    }\n}