public class Company\n{\n    public string Name { get; set; }\n    public IList<Employee> Employees { get; private set; }\n    public int ContactPersonId { get; set; }\n\n    public Employee ContactPerson \n    {\n        get \n        {\n            return Employees.SingleOrDefault(e => e.Id == ContactPersonId);\n        }\n    }\n    \n    public void SetContactPerson(Employee employee)\n    {\n        if (Employees.Contains(employee))\n        {\n            ContactPersonId = employee.Id;\n        }\n    }\n}\n\npublic class Employee\n{\n    public int Id { get; set; }\n    public int CompanyId { get; set; }\n    public string FullName { get; set; }\n\n    // You might need a navigation property for ORM purposes\n    // public Company EmployedBy { get; set; }\n}